import { Component, OnInit } from '@angular/core';
import { Product } from 'src/app/common/product';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit{
    products: Product[] = [];

    constructor(private productService: ProductService){ }

    ngOnInit(): void {
        this.listProducts(); 
    }

    listProducts() {
        this.productService.getProductList().subscribe(
            data => {
                this.products = data;
            }
        )
    }

}
<div class="container text-center">
    <div class="row">
      <div class="col-6 col-md-4 col-sm product" *ngFor = "let product of products">
        <a href = "#">
            <img src = "{{product.imageUrl}}" class="img-fluid rounded wiggle" alt="{{ product.name }}"/>
        </a>
        <a href = "#">
            <h4 class = "product_name">{{ product.name }}</h4>
        </a>
        <p class = "product_desc">{{ product.unitPrice | currency :'USD' }}</p>
        <a href="#" class="primary-btn">Add to cart</a>
      </div>
    </div>
</div>

<div class="footer-pagination">
    <div class="row">
        <div class="col-md-6"></div>
        <div class="col-md-3">
            <div class="items">
                <label>Item per page</label> <select>
                    <option>10</option>
                    <option>20</option>
                    <option>30</option>
                    <option>40</option>
                </select>
            </div>
        </div>
        <div class="col-md-2">
            <div class="number">1 - 10 of 100</div>
        </div>
        <div class="col-md-1">
            <div class="arrows">
                <a href="#"><i class="fa fa-angle-left" aria-hidden="true"></i></a>
                <a href="#"><i class="fa fa-angle-right" aria-hidden="true"></i></a>
            </div>
        </div>
    </div>
</div>
